/*Table structure for table `order` */

DROP TABLE IF EXISTS `order`;

CREATE TABLE `order` (
  `pid` int(11) NOT NULL AUTO_INCREMENT COMMENT '病人id',
  `name` varchar(20) NOT NULL COMMENT '病人性别',
  `sex` char(4) DEFAULT NULL COMMENT '病人姓名',
  `age` int(11) NOT NULL COMMENT '病人年龄',
  `department` varchar(50) DEFAULT NULL COMMENT '预约的科室',
  `doctorName` varchar(50) DEFAULT NULL COMMENT '预约医生的姓名',
  `phone` varchar(11) DEFAULT NULL COMMENT '病人的联系电话',
  `orderTime` date DEFAULT NULL COMMENT '预约的时间',
  `doctorId` int(11) DEFAULT NULL COMMENT '医生id',
  `status` int(11) DEFAULT NULL COMMENT '预约状态',
  `uid` int(11) DEFAULT NULL,
  `rstatus` varchar(11) DEFAULT NULL,
  PRIMARY KEY (`pid`),
  KEY `FK_order` (`doctorId`),
  KEY `FK_order1` (`uid`),
  CONSTRAINT `FK_order` FOREIGN KEY (`doctorId`) REFERENCES `doctor` (`did`),
  CONSTRAINT `FK_order1` FOREIGN KEY (`uid`) REFERENCES `user` (`uid`)
