/*Table structure for table `record` */

DROP TABLE IF EXISTS `record`;

CREATE TABLE `record` (
  `rid` int(11) NOT NULL AUTO_INCREMENT,
  `did` int(11) DEFAULT NULL,
  `pid` int(11) DEFAULT NULL,
  `symptom` varchar(1000) DEFAULT NULL,
  `diagnosis` varchar(1000) DEFAULT NULL,
  `prescription` varchar(1000) DEFAULT NULL,
  `createdate` varchar(100) DEFAULT NULL,
  `rstatus` int(255) NOT NULL,
  PRIMARY KEY (`rid`),
  KEY `fk_rp` (`pid`),
  KEY `fk_rd` (`did`),
  CONSTRAINT `FK_record` FOREIGN KEY (`did`) REFERENCES `doctor` (`did`)
) ENGINE=InnoDB AUTO_INCREMENT=28 DEFAULT CHARSET=utf8;

/*Data for the table `record` */

insert  into `record`(`rid`,`did`,`pid`,`symptom`,`diagnosis`,`prescription`,`createdate`,`rstatus`) values (1,1,1,'上呼吸道感染简称上感，又称普通感冒。是包括鼻腔、咽或喉部急性炎症的总称。广义的上感不是一个疾病诊断，而是一组疾病，包括普通感冒、病毒性咽炎、喉炎、疱疹性咽峡炎、咽结膜热、细菌性咽-扁桃体炎。狭义的上感又称普通感冒，是最常见的急性呼吸道感染性疾病，多呈自限性，但发生率较高。成人每年发生2～4次，儿童发生率更高，每年6～8次。全年皆可发病，冬春季较多。','具有清热解毒和抗病毒作用的中药亦可选用，有助于改善症状，缩短病程。小柴胡冲剂、板蓝根冲剂应用较为广泛。','除菌喷剂','20170909',1),(2,2,2,'上呼吸道感染简称上感，又称普通感冒。是包括鼻腔、咽或喉部急性炎症的总称。广义的上感不是一个疾病诊断，而是一组疾病，包括普通感冒、病毒性咽炎、喉炎、疱疹性咽峡炎、咽结膜热、细菌性咽-扁桃体炎。狭义的上感又称普通感冒，是最常见的急性呼吸道感染性疾病，多呈自限性，但发生率较高。成人每年发生2～4次，儿童发生率更高，每年6～8次。全年皆可发病，冬春季较多。','具有清热解毒和抗病毒作用的中药亦可选用，有助于改善症状，缩短病程。小柴胡冲剂、板蓝根冲剂应用较为广泛。','除菌喷剂','20170909',2),(3,3,3,'龋病俗称虫牙、蛀牙，是细菌性疾病，可以继发牙髓炎和根尖周炎，甚至能引起牙槽骨和颌骨炎症。如不及时治疗，病变继续发展，形成龋洞，终至牙冠完全破坏消失，其发展的最终结果是牙齿丧失。龋病特点是发病率高，分布广。是口腔主要的常见病，也是人类最普遍的疾病之一，世界卫生组织已将其与肿瘤和心血管疾病并列为人类三大重点防治疾病。','龋病治疗的目的在于终止病变过程，恢复牙齿的固有形态和功能。','西帕依固龈液','20180101',3),(4,4,4,'发热症状描述：诱因，发热，最高体温，热型，持续时间，是否需药物干预，\n有无畏寒、寒战，有无皮疹，有无关节痛，有无午后潮热盗汗，有无体重明显减轻，有无口腔溃疡，有无脱发，有无咳嗽咳痰，有无解稀烂便。 ','具有清热解毒和抗病毒作用的中药亦可选用，有助于改善症状，缩短病程。小柴胡冲剂、板蓝根冲剂应用较为广泛。','除菌喷剂','20170909',1),(5,5,5,'发热体征描述：结膜充血、单纯疱疹、淋巴结肿大、肝脾肿大、出血、关节\n肿痛、皮疹、昏迷。','具有清热解毒和抗病毒作用的中药亦可选用，有助于改善症状，缩短病程。小柴胡冲剂、板蓝根冲剂应用较为广泛。','除菌喷剂','20170909',2),(6,6,6,'水肿体征描述：HR，R，甲状腺体征，有无发绀、黄疸、肝颈静脉回流征，\n心界、肝肿大、心跳缓慢，双下肢对称性轻/中/重度凹陷性浮肿。','龋病治疗的目的在于终止病变过程，恢复牙齿的固有形态和功能。','西帕依固龈液','20180101',3),(7,7,7,'血尿体征描述：肾区叩痛、水肿、肾肿块、皮肤黏膜及其他部位出血。','具有清热解毒和抗病毒作用的中药亦可选用，有助于改善症状，缩短病程。小柴胡冲剂、板蓝根冲剂应用较为广泛。','除菌喷剂','20170909',1),(8,8,8,'由于过度疲劳、精神紧张、姿势不良等原因引起颅顶肌、颞肌和颈肌持续收缩而产生的慢性头痛。','具有清热解毒和抗病毒作用的中药亦可选用，有助于改善症状，缩短病程。小柴胡冲剂、板蓝根冲剂应用较为广泛。','除菌喷剂','20170909',2),(9,9,9,'血尿体征描述：肾区叩痛、水肿、肾肿块、皮肤黏膜及其他部位出血。','龋病治疗的目的在于终止病变过程，恢复牙齿的固有形态和功能。','西帕依固龈液','20180101',3),(10,10,10,'血尿体征描述：肾区叩痛、水肿、肾肿块、皮肤黏膜及其他部位出血。','具有清热解毒和抗病毒作用的中药亦可选用，有助于改善症状，缩短病程。小柴胡冲剂、板蓝根冲剂应用较为广泛。','除菌喷剂','20170909',1),(11,11,11,'血尿体征描述：肾区叩痛、水肿、肾肿块、皮肤黏膜及其他部位出血。','具有清热解毒和抗病毒作用的中药亦可选用，有助于改善症状，缩短病程。小柴胡冲剂、板蓝根冲剂应用较为广泛。','除菌喷剂','20170909',2),(12,12,12,'血尿体征描述：肾区叩痛、水肿、肾肿块、皮肤黏膜及其他部位出血。','龋病治疗的目的在于终止病变过程，恢复牙齿的固有形态和功能。','西帕依固龈液','20180101',3),(13,13,13,'血尿体征描述：肾区叩痛、水肿、肾肿